mapper.insert:
INSERT INTO MEMBER (
    MEM_EMAIL
    , MEM_PW
    , MEM_NAME
) VALUES (
    #{memEmail}
    , #{memPw}
    , #{memName}
)

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEM_EMAIL = #{memEmail}

mapper.update:
UPDATE MEMBER
SET WARNING_CNT = WARNING_CNT + 1
WHERE MEM_ID = #{reportedId};

UPDATE MEMBER
SET MEM_STATUS = #{status}
    , BANNED_UNTIL = NULL
WHERE MEM_ID = #{memId}
